export function getAllPokemons() {
  return (dispatch) => {
    axios.get(apiServe)
      .then((response) => {
        console.log(response);
        dispatch(getPokemon(response.data))
      }).catch((error) => {
        dispatch(getPokemonError(error))
      })
  }
}
