const initialState = {
  sending: false,
  pokemon: null,
}

export default function searchPokemon(state = initialState, action) {
  switch(action.type) {
    case 'POKEMON_SENDING':
      return {
        ...state,
        sending: true
      }
      case 'POKEMON_SEARCH':
        return {
          ...state,
          sending: false,
          pokemons: action.payload,
        }
      case 'POKEMON_ERROR':
        return {
          ...state,
          sending: false
        }
      default:
        return state;
  }
}
