import React, { Component } from 'react'
import { connect } from 'react-redux';
import Nav from '../../components/Navbar/Navbar';
import Card from '../../components/Card/Card';
import Search from '../../components/Search/Search';
import CardList from '../../components/CardList/CardList';
// import { ContainerPokedex } from './PokedexStyle';

// import * as actionCreators from '../../store/actions/index';
import * as actionCreators from '../../store/actions/search';

class Pokedex extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: '',
      imagePokemon: null,
      loading: true,
      allPokemons: [],
    }    
  }

  componentWillMount() {
    console.log(this.props.actions)
    this.props.getAllPokemons();
  }

  componentDidMount() {
  }

  componentDidUpdate() {
    console.log(this.props.payload)
  }

  handleSearchInput(e) {
    this.setState({ search: e.target.value });
  }

  handleGetImage() {
    const { payload } = this.props;
    this.setState({ imagePokemon: payload.img.front_default });
  }

  render() {
    const { search, imagePokemon, allPokemons } = this.state;
    const { payload } = this.props;
    console.log(this.props.payload)
    return (
     <div className="d-flex align-items-center justify-content-center">
      <h2>A live já vai começar</h2>
     </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Pokedex);
