import React, { Component } from 'react'
import { connect } from 'react-redux';
import MaterialCard from '../../components/MaterialCard/MaterialCard';
import Layout from '../../components/Layout/Layout';
import If from '../../utils/If/If';
import CircularProgress from '@material-ui/core/CircularProgress';
import { Grid, Paper, Typography } from '@material-ui/core';
import Card from '../../components/Card/Card';

import * as actionCreators from '../../store/actions/getGeneration';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: true,
      generations: [],
    }
  }

  componentDidMount() {
    this.props.showAllGeneration()
    setTimeout(() => {
      this.showAllGeneration()
      this.setState({ loading: false })
    }, 500);
  }

  componentDidUpdate() {
    // console.log(this.state.generations)
  }

  showAllGeneration() {
    const { generations } = this.state;
    const { generation } = this.props;
    generations.push(generation.payload.generation);
  }
  
  render() {
    const { loading, generations } = this.state;
    const { pokemon } = this.props;
    console.log(generations)
    return (
      <Grid style={{ flexGrow: 1 }}container justify="center" alignItems="center">

        <If show={loading}>
          <Grid style={{ flexGrow: 1 }}container justify="center" alignItems="center">
            <CircularProgress aria-describedby color="secondary"/>
          </Grid>
        </If>

        <If show={!loading}>
          <Grid justify="center" container >
            <Grid item sm={4}>
              <Card 
                generations={generations}
              />
            </Grid>
          </Grid>
        </If>
      </Grid>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Home);
