import React, { Component } from 'react'
import { connect } from 'react-redux';
import MaterialCard from '../../components/MaterialCard/MaterialCard';
import Layout from '../../components/Layout/Layout';

import * as actionCreators from '../../store/actions/getPokemon';

class Pokedex extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: '',
      imagePokemon: null,
      loading: true,
      allPokemons: [],
    }    
  }

  componentDidMount() {
    this.props.getAllPokemons();
    
  }

  componentDidUpdate() {
    console.log(this.state.allPokemons)
  }
  
  showAllPokemons() {
    const { allPokemons } = this.state;
    const { pokemon } = this.props;
    this.setState({ allPokemons: pokemon });

 
    
  }
  
  handleSearchInput(e) {
    this.setState({ search: e.target.value });
  }
  
  render() {
    const { pokemon } = this.props;

    return (
      <Layout 
        title="AwesomePokedex"
        pokemons={pokemon.payload}
      />
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Pokedex);
