import React, { Component } from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
// import { Link } from 'react-router-dom';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as actionCreators from '../../store/actions/index';
// import { pokemonSearch } from '../../store/actions/Search/actions';

import Header from '../../components/Header/Header';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newText: 'pikachu',
    }
    console.log(props)
  }

  componentDidUpdate() {
    console.log(this.props.addItems.text)
  }

  addNewText() {
    // const { addItems,  searchPokemon } = this.props;
    // const { newText } = this.state;
    // addItems(newText);
    // console.log(searchPokemon(newText))
    // console.log( addItems(newText))
    // // this.setState({ newText: ''});
  }

  inputChange(e) {
    // const { newText } = this.state;
    this.setState({ newText: e.target.value })
  }
  
  render() {
    return (
      <div>
      <Header 
        titleBrand="Awesome Pokedex"
        labelButton="Buscar"
        value={this.state.newText}
        onChange={(e) => this.inputChange(e)}
      />
      <p>{this.props.pokemon}</p>
      {/* <Link to="/pokedex"> */}
        <ButtonSearch onClick={() => this.props.searchPokemon()} backgroundcolor="green" color="white"> Pokedex</ButtonSearch>
      {/* </Link> */}

      </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Home);
