import React, { Component } from 'react'
import { connect } from 'react-redux';
import Header from '../../components/Header/Header';
import Nav from '../../components/Navbar/Navbar';
import Card from '../../components/Card/Card';
import Search from '../../components/Search/Search';
import { ContainerPokedex } from './PokedexStyle';

import * as actionCreators from '../../store/actions/index';

class Pokedex extends Component {
  constructor(props) {
    super(props);
    console.log(props);
    this.state = {
      search: '',
      imagePokemon: null,
      loading: this.props.loading,
    }
  }

  handleSearchInput(e) {
    this.setState({ search: e.target.value });
  }

  handleGetImage() {
    const { payload } = this.props;
    this.setState({ imagePokemon: payload.img.front_default });
  }

  render() {
    const { search, imagePokemon } = this.state;
    const { payload } = this.props;
    return (
      <ContainerPokedex fluid>
        <Nav  />
        <Search 
          placeholder="search"
          value={search}
          onChange={(e) => this.handleSearchInput(e)}
          onClick={() => {
            this.props.searchPokemonForName(search);
            setTimeout(() => {
              this.handleGetImage();
            }, 1000);
          }} 
        />
        <Card loading={payload.loading} pokemon={payload} srcImage={imagePokemon}/>
      </ContainerPokedex>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Pokedex);
