import React from 'react';
import PropTypes from 'prop-types';
import {
  PokemonCard,
  PokemonCardContent,
  PokemonCardFooter,
  PokemonCardMedia,
  PokemonCardBody,
  // ButtonInfo,
  Typographys,
  styles,
} from './MaterialCardStyle';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';

const MaterialCard = ({
    pokemons,
}) => (
  <Grid container>
    {
      pokemons.map(item => (
        item.pokemon.map(pokemon => (
          <Grid item xs={12} sm={3} key={pokemon.id}>
            <PokemonCard style={styles.card}>
                <PokemonCardContent>
    
                <PokemonCardMedia
                  style={styles.media}
                  title="Contemplative Reptile"
                  image={pokemon.sprites.front_default}
                  // src={pokemon.sprites.front_shiny}
                />

                <PokemonCardBody>
                  <Typographys align="center" variant="h5" component="h2">
                    {pokemon.name}
                  </Typographys>
                </PokemonCardBody>
              </PokemonCardContent>
              <PokemonCardFooter />
            </PokemonCard>
          </Grid>
        ))
      )) 
    }

  </Grid>
);

MaterialCard.propTypes = {
  pokemons: PropTypes.array,
}

export default MaterialCard;
