export async function getAllPokemons() {
  return async (dispatch) => {
    try {
      const data = await axios.get(`${host}pokemon`);
      await data.data.results.map((pokemon) => {
        return pokemon.url;
      }).map(async (pokemon) => {
        const responsePokemon = await axios.get(pokemon);
        dispatch(getPokemon(responsePokemon));
        console.log(responsePokemon.data);
      })
    } catch (e) {
      console.error(e.message);
    }
  };
}

export function searchPokemonForName(pokemon) {
  return (dispatch) => {
    axios.get(apiServe + pokemon).then((response) => {
      dispatch(getPokemonForName(response.data));
    });
  };
}
