const host = 'https://pokeapi.co/api/v2/';

async function getAllPokemons() {
  try {
    let response = await fetch(`${host}pokemon`);
    let data = await response.json();
    
    await Array.from(data.results).map((el) => {
      return el.url
    }).map(async (el) => {
      let response = await fetch(el);
      let data = await response.json();
      console.log(data)
    })
  } catch (e) {
    console.error(e.message)
  }
}


export function searchPokemonForName(pokemon) {
  return (dispatch) => {
    axios.get(apiServe + pokemon).then((response) => {
      dispatch(getPokemonForName(response.data));
    });
  };
}
