import React from 'react';
import PropType from 'prop-types';
import { 
  CardPokemon,
  CardButton,
  CardContainer,
  CardGrid,
  CardRow,
} from './CardStyle';
import If from '../../utils/If/If';

const Card = ({
    pokemon,
    description,
    srcImage,
    loading,
}) => (
  <If show={loading === false}>
    <CardContainer className="d-flex justify-content-center">
      <CardRow>
        <CardGrid xs="12" sm="12">
          <CardPokemon  style={{ width: '18rem' }}>
            <CardPokemon.Img variant="top" cap src={srcImage} />
            <CardPokemon.Body>
              <CardPokemon.Title>{pokemon.name}</CardPokemon.Title>
              <CardPokemon.Text>{description}</CardPokemon.Text>
              {/* <CardButton variant="primary">Go somewhere</CardButton> */}
            </CardPokemon.Body>
          </CardPokemon>
        </CardGrid>
      </CardRow>  
    </CardContainer>
    
  </If>
);

Card.propTypes = {
  pokemons: PropType.array,
  title: PropType.string,
  description: PropType.string,
  srcImage: PropType.string,
};

export default Card;
