import React, { Component } from 'react'
import { connect } from 'react-redux';
import Header from '../../components/Header/Header';
import Card from '../../components/Card/Card';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as actionCreators from '../../store/actions/index';

class Pokedex extends Component {
  constructor(props) {
    super(props);
    console.log(props);
    this.state = {
      search: '',
      imagePokemon: null,
    }
  }

  handleSearchInput(e) {
    this.setState({ search: e.target.value });
  }

  handleGetImage() {
    const { img } = this.props;
    this.setState({ imagePokemon: img.front_shiny });
  }

  render() {
    const { search, imagePokemon } = this.state;
    const { pokemon, img } = this.props;
    console.log(search)
    console.log(img)
    console.log(pokemon)
    return (
      <div>
        <Header 
          titleBrand="Awesome Pokedex" 
          labelButton="Buscar" 
          value={search}
          onChange={(e) => this.handleSearchInput(e)}
          />
        <ButtonSearch 
          onClick={() => {
            this.props.searchPokemonForName(search);
            setTimeout(() => {
              this.handleGetImage();
            }, 1000);
          }} 
          backgroundcolor="green" 
          color="white"
        >
          Teste
        </ButtonSearch>
        <Card pokemon={pokemon.payload} srcImage={imagePokemon}/>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Pokedex);
