import React, { Component } from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as addItemsAction from '../../store/actions/addItems';
import Header from '../../components/Header/Header';

class Home extends Component {
  constructor(props) {
    super(props);

    console.log(props);
  }

  state = {
    newText: '',
  };

  addNewText = () => {
    const { addItems } = this.props;
    const { newText } = this.state;
    addItems(newText);
    this.setState({ newText: ''});

  }
  
  render() {
    return (
      <div>
      <Header 
        titleBrand="Awesome Pokedex"
        labelButton="Buscar"
      />
      <p>{this.props.addItems.text}</p>
      <Link to="/pokedex">
        <ButtonSearch onClick={this.addNewText()} backgroundcolor="green" color="white"> Pokedex</ButtonSearch>
      </Link>

      </div>
    )
  }
}

const mapStateToProps = state => ({
  todos: state.todos,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(addItemsAction, dispatch);

  export default connect(mapStateToProps, mapDispatchToProps)(Home);
