import React, { Component } from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as addItemsAction from '../../store/actions/addItems';
import Header from '../../components/Header/Header';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newText: '',
    }

    // console.log(props.addItems.text);
  }

  componentDidUpdate() {
    // console.log(this.state.newText)
    console.log(this.props.addItems.text)
  }

  addNewText() {
    const { addItems } = this.props;
    const { newText } = this.state;
    addItems(newText);
    this.setState({ newText: ''});
  }
  
  render() {
    return (
      <div>
      <Header 
        titleBrand="Awesome Pokedex"
        labelButton="Buscar"
        value={this.state.newText}
        onChange={(e) => this.setState({ newText: e.target.value })}
      />
      <ul>
      { this.props.addItems.map(todo => (
            <li key={todo.id}>{todo.text}</li>
          )) }
      </ul>
      {/* <Link to="/pokedex"> */}
        <ButtonSearch onClick={() => this.addNewText()} backgroundcolor="green" color="white"> Pokedex</ButtonSearch>
      {/* </Link> */}

      </div>
    )
  }
}

const mapStateToProps = state => ({
  todos: state.todos,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(addItemsAction, dispatch);

  export default connect(mapStateToProps, mapDispatchToProps)(Home);
