import React, { Component } from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as addItemsAction from '../../store/actions/addItems';
import Header from '../../components/Header/Header';

class Home extends Component {
  constructor(props) {
    super(props);

    console.log(props)
  }

  state = {
    newText: '',
  }

  componentDidUpdate() {
    console.log(this.props.addItems.text)
  }

  addNewText() {
    const { addItems } = this.props;
    const { newText } = this.state;
    addItems(newText);
    console.log( addItems(newText))
    // this.setState({ newText: ''});
  }

  inputChange(e) {
    // const { newText } = this.state;
    this.setState({ newText: e.target.value })
  }
  
  render() {
    const { addItems, text } = this.props;
    console.log(addItems.text);
    console.log(this.state.newText)
    return (
      <div>
      <Header 
        titleBrand="Awesome Pokedex"
        labelButton="Buscar"
        value={this.state.newText}
        onChange={(e) => this.inputChange(e)}
      />
      <ul>
        {
          Object.keys(addItems).map((items) => (
            <li>{items.text}</li>
          ))
        }
      </ul>
      {/* <Link to="/pokedex"> */}
        <ButtonSearch onClick={() => this.addNewText(this.state.newText)} backgroundcolor="green" color="white"> Pokedex</ButtonSearch>
      {/* </Link> */}

      </div>
    )
  }
}

const mapStateToProps = store => ({
  addItems: store.addItems.text,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(addItemsAction, dispatch);

  export default connect(mapStateToProps, mapDispatchToProps)(Home);
