export function getAllPokemons() {
  return (dispatch) => {
    axios.get(apiServe)
      .then((response) => {
        dispatch(getPokemon(response.data.results));
      })
      .then((data) => {
        console.log(data)
      })
  }
}

export function searchPokemonForName(pokemon) {
  return (dispatch) => {
    axios.get(apiServe + pokemon).then((response) => {
      dispatch(getPokemonForName(response.data));
    });
  };
}
