import React, { Component } from 'react'
import { connect } from 'react-redux';
import If from '../../utils/If/If';
import { Grid } from '@material-ui/core';
import Navbar from '../../components/Navbar/Navbar';
import Footer from '../../components/Footer/Footer';
import GenerationCard from '../../components/Card/GenerationCard';
import SpinnerIf from '../../components/SpinnerIf/SpinnerIf';
import { Container } from './HomeStyle';

import * as actionCreators from '../../store/actions/getGeneration';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: true,
      generations: [],
    }
  }

  componentDidMount() {
    this.props.showAllGeneration()
    setTimeout(() => {
      this.showAllGeneration()
      this.setState({ loading: false })
    }, 500);
  }

  componentDidUpdate() {
    // console.log(this.state.generations)
  }

  showAllGeneration() {
    const { generations } = this.state;
    const { generation } = this.props;
    generations.push(generation.payload.generation);
  }
  
  render() {
    const { loading, generations } = this.state;

    return (
      <div>
        <Navbar title="AwesomePokedex" background="coral" />
        <Container container spacing={16} justify="center" alignItems="center">
        <SpinnerIf show={loading} />

        <If show={!loading}>
          <Grid item xs={12}>
            <GenerationCard 
              generations={generations}
            />
          </Grid>
        </If>
        <Footer />
        </Container>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Home);
