import axios from 'axios';

const url = 'https://pokeapi.co/api/v2/pokedex/';
const id = [1, 2, 3, 4, 5, 6];

export function getGeneration(generation) {
  return {
    type: 'GET_POKEMON',
    generation,
  };
}

export function getGenerationError(error) {
  return {
    type: 'GET_POKEMON_ERROR',
    error,
  };
}


