import React, { Component } from 'react'
import { connect } from 'react-redux';
import Header from '../../components/Header/Header';
import Card from '../../components/Card/Card';
import { ButtonSearch } from '../../components/Buttons/Button';

import * as actionCreators from '../../store/actions/index';

class Pokedex extends Component {
  constructor(props) {
    super(props);
    console.log(props);
    this.state = {
      search: '',
    }
  }

  handleSearchInput(e) {
    this.setState({ search: e.target.value });
  }

  render() {
    const { search } = this.state;
    const { pokemon, img } = this.props;
    console.log(search)
    console.log(img.front_default)
    console.log(pokemon)
    return (
      <div>
        <Header 
          titleBrand="Awesome Pokedex" 
          labelButton="Buscar" 
          value={search}
          onChange={(e) => this.handleSearchInput(e)}
          />
        <ButtonSearch 
          onClick={() => this.props.searchPokemonForName(search)} 
          backgroundcolor="green" 
          color="white"
        >
          Teste
        </ButtonSearch>
        <Card pokemon={pokemon} srcImage={img}/>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Pokedex);
