import React from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import { Grid } from '@material-ui/core';
import { colors } from '../../styles/styles';

const SpinnerIf = (props) => {
  const {
    show, color, description, textStyle,
  } = props;

  if (show) {
    return (
      <Grid style={{ flexGrow: 1 }} container justify="center" alignItems="center" spacing={16}>
        <Grid item xs={12}>
          <CircularProgress aria-describedby color={color || colors.primary}/> 
        </Grid>
      </Grid>
    );
  }
  return false;
};

export default SpinnerIf;
