import React from 'react';
import PropTypes from 'prop-types';
import {
  Typographys,
  styles,
  Card,
  Image,
  ImageContent,
  CardBody,
  CardFooter,
  PokemonType,
} from './MaterialCardStyle';
import Grid from '@material-ui/core/Grid';
import { colors } from '../../styles/styles';

const MaterialCard = ({
    pokemons,
}) => (
  <Grid align="center" container style={styles.root}>
    {
      pokemons.map((item) => (
        item.pokemon.map((pokemon) => (
          <Grid item xs={12} sm={2} key={pokemon.id}>
            <Card style={styles.cardPokemon} elevation={2}>
              <Typographys align="right" variant="body1">#{pokemon.id}</Typographys>
              <ImageContent>
                <Image src={`${item.image}${pokemon.name}.gif`} />
              </ImageContent>
                <Typographys variant="title">{pokemon.name}</Typographys>
              <CardBody>
                {
                  pokemon.types.map(type => (
                    <PokemonType label={type.type.name} 
                      background={
                        type.type.name === 'poison' ? colors.poison : null
                        || type.type.name === 'grass' ? colors.green : null
                        || type.type.name === 'bug' ? colors.lightGreen : null
                        || type.type.name === 'fire' ? colors.red : null
                        || type.type.name === 'water' ? colors.water : null
                        || type.type.name === 'flying' ? colors.flying : null
                        || type.type.name === 'normal' ? colors.grey : null
                        || type.type.name === 'ground' ? colors.normal : null
                        || type.type.name === 'eletric' ? colors.eletric : null
                        || type.type.name === 'fairy' ? colors.fairy : null
                        || type.type.name === 'fighting' ? colors.brown : null
                        || type.type.name === 'psychic' ? colors.ghost : null
                      }
                    />
                  ))
                }
              </CardBody>
              <CardFooter>
                {/* <Typographys className="pokemon-number" align="right" variant="body1">#{pokemon.id}</Typographys> */}
              </CardFooter>
            </Card>
          </Grid>
        ))
      ))
    }
  </Grid>
);

MaterialCard.propTypes = {
  pokemons: PropTypes.array,
}

export default MaterialCard;
