let defaultState = {
  pokemon: [],
}

const mainReducer = (state = defaultState, action) => {
  switch (action.type) {
    case 'POKEMON_SEARCH':
    return {
      ...state,
      pokemon: action.pokemon,
    };
    case 'POKEMON_SEARCH_SUCCESS':
    return {
      ...state,
      pokemon: action.pokemon,
      img: action.pokemon.sprites,
    };
    default:
    return {
      ...state,
    }
  }
}

export default mainReducer;
