import axios from 'axios';

const apiServe = 'https://pokeapi.co/api/v2/pokemon/';
const apiImage = 'http://pokestadium.com/sprites/xy/';
// const offSet = 50;
const limitNumber = 50;
const limit = `?limit=${limitNumber}`;
const pokemons = [];
const pokemonConfig = [];


export function getPokemon(pokemon, image) {
  return {
    type: 'GET_POKEMON',
    pokemon,
    image,
  };
}

export function getPokemonError(error) {
  return {
    type: 'GET_POKEMON_ERROR',
    error,
  };
}

export function pokemonPagination(limitNumber) {
  return {
    type: 'SHOW_PAGINATION',
    limitNumber,
  };
}



export function getAllPokemons() {
  return (dispatch) => {
    updatePokemonPagination(100)
    axios.get(apiServe + limit)
      .then((response) => {
        const data = response.data.results;
        data.map(pokemon => pokemons.push(pokemon.url));
        pokemons.forEach((pokemon) => {
          axios.get(pokemon)
            .then((res) => {
              const abi = res.data;
              pokemonConfig.push(abi);
              pokemonConfig.sort((a, b) => a.id - b.id);
              dispatch(getPokemon(pokemonConfig, apiImage));
            }).catch((error) => {
              dispatch(getPokemonError(error));
            });
        });
      }).catch((error) => {
        console.error(error);
        dispatch(getPokemonError(error));
      });
  };
}

export function updatePokemonPagination(limitNumber) {
  return (dispatch) => {
    console.log('estou funcionando');
    dispatch(pokemonPagination(limitNumber));
    getAllPokemons();
  };
}
