import React from 'react';
import PropType from 'prop-types';
import { CardDeck, Card, Col, Alert } from 'react-bootstrap';
import { Badge } from './CardListStyle';
import { colors } from '../../styles/styles';


const CardList = ({
    pokemons,
    // srcImage,
}) => (
  <CardDeck>
    {
      pokemons.map(pokemon => (
        <Col key={pokemon.name} sm="2">
          <Card>
            <Card.Img variant="top"  src={pokemon.sprites.front_default}/>
              <Card.Body>
                <Card.Title className="text-center" >{pokemon.name}</Card.Title>
                <Card.Text>                  
                  {/* { 
                    pokemon.types.map(type => <Alert className="text-center" variant="primary" key={type.slot}>{type.type.name}</Alert>)
                  } */}
                </Card.Text>
              </Card.Body>
            <Card.Footer>
              <small className="text-muted">
              { 
                pokemon.types.map(type => (
                  <Badge
                    className="text-center"
                    background={
                      type.type.name === 'poison' ? colors.lightGreen : null
                      || type.type.name === 'water' ? colors.blue : null 
                      || type.type.name === 'fire' ? colors.red : null
                      || type.type.name === 'bug' ? colors.darkGreen : null
                      || type.type.name === 'grass' ? colors.lemonGreen : null 
                      || type.type.name === 'flying' ? colors.lightBlue : null 
                      || type.type.name === 'normal' ? colors.light : null 
                    }
                    color={type.type.name === 'normal' ? colors.dark : null }
                    key={type.slot}
                  >  
                    {type.type.name}
                  </Badge>
                ))
              }
              </small>
            </Card.Footer>
          </Card>
        </Col>
      )) 
    }
  </CardDeck>
);

CardList.propTypes = {
  pokemons: PropType.array,
  // title: PropType.string,
  // description: PropType.string,
  // srcImage: PropType.string,
};

export default CardList;
