import axios from 'axios';

const apiServer = 'https://pokeapi.co/api/v2/pokemon/';

export const pokemonSearch = (payload = null) => ({
  type: 'POKEMON_SEARCH',
  payload: payload
})

export const searchAction = pokemon => {
  return axios.get(apiServer + pokemon).then(response => {
    return pokemonSearch(response.data);
  })
  .catch(e => {
    return console.log(e);
  })
}
