const initialState = {
  loading: false,
  pokemon: [],
  error: null,
}

export default function searchPokemon(state = initialState, action) {
  switch(action.type) {
    case 'POKEMON_SENDING':
      return {
        ...state,
        loading: true
      }
      case 'POKEMON_SEARCH_SUCCESS':
        return {
          ...state,
          loading: false,
          pokemon: action.payload,
        }
      case 'POKEMON_ERROR':
        return {
          ...state,
          loading: false
        }
      default:
        return state;
  }
}
