import React, { Component } from 'react'
import { connect } from 'react-redux';
import MaterialCard from '../../components/MaterialCard/MaterialCard';
import Layout from '../../components/Layout/Layout';
import If from '../../utils/If/If';
import CircularProgress from '@material-ui/core/CircularProgress';
import { Grid } from '@material-ui/core';

import * as actionCreators from '../../store/actions/getGeneration';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: true,
      loading: true,
    }
  }

  componentDidMount() {
    this.props.showAllGeneration()
  }

  componentDidUpdate() {
  }
  
  render() {
    const { loading, allPokemons } = this.state;
    const { pokemon } = this.props;

    return (
      <div>
        <If show={loading}>
        <Grid style={{ flexGrow: 1 }}container justify="center" alignItems="center">
          <CircularProgress aria-describedby color="secondary"/>
        </Grid>
        </If>
        {/* <If show={!loading}>
          <Layout 
            title="AwesomePokedex"
            pokemons={allPokemons}
          />
        </If> */}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps, actionCreators)(Home);
