import axios from 'axios';

const apiServe = 'https://pokeapi.co/api/v2/pokemon/';
const limit = '?offset=0&limit=40';
const pokemons = [];
const pokemonConfig = [];

export function getPokemon(pokemon) {
  return {
    type: 'GET_POKEMON',
    pokemon,
  };
}

export function getPokemonError(error) {
  return {
    type: 'GET_POKEMON_ERROR',
    error,
  };
}

export function getAllPokemons() {
  return (dispatch) => {
    axios.get(apiServe + limit)
      .then((response) => {
        const data = response.data.results;
        data.map(pokemon => pokemons.push(pokemon.url));
        pokemons.forEach((pokemon) => {
          axios.get(pokemon)
            .then((res) => {
              const abi = res.data;
              pokemonConfig.push(abi);
              dispatch(getPokemon(pokemonConfig));
            }).catch((error) => {
              dispatch(getPokemonError(error));
            });
        });
      }).catch((error) => {
        console.log(error);
        dispatch(getPokemonError(error));
      });
  };
}
