export function getAllPokemons() {
  return (dispatch) => {
    axios.get(apiServe)
      .then((response) => {
        const data = response.data.results;
        data.map(pokemon => pokemons.push(pokemon));
        console.log(pokemons)
        // dispatch(getPokemon(response.data.results))
      }).catch((error) => {
        dispatch(getPokemonError(error))
      })
  }
}
